import { useGetSpotifyAlbumQuery } from "./app/apiSlice";
import { useLocation } from "react-router-dom";

const AlbumInfo = () => {
  const location = useLocation();
  const { data } = location.state;

  const { data: albums, isLoading } = useGetSpotifyAlbumQuery(
    data.albums["id"]
  );

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }

  return (
    data && (
      <div className="row mt-3">
        <h1>
          <img
            src={albums[0]["album_info"]["image"]}
            width="200px"
            height="200px"
          ></img>
        </h1>
        <h1>{albums[0]["album_info"]["name"]}</h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>#</th>
              <th>Name</th>
              <th>Length</th>
            </tr>
          </thead>
          <tbody>
            {albums[0]?.tracks.map((tracks) => {
              return (
                <tr key={tracks.id}>
                  <td>{tracks.track_number}</td>
                  <td>{tracks.name}</td>
                  <td>{tracks.duration_ms}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    )
  );
};
export default AlbumInfo;
