import { useGetSpotifyTrackQuery, useGetAllPlaylistQuery } from "./app/apiSlice";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import { useSelector } from "react-redux";
import { Dropdown } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import CreateSongButton from "./playlist_functions/CreateSong.js";





const SongInfo = () => {
  const location = useLocation();
  const { data } = location.state;

  const { data: track, isLoading } = useGetSpotifyTrackQuery(data.track.id);
  const { data: playLists } = useGetAllPlaylistQuery();
  const [selectPlayList, setSelectPlayList] = useState('');
  const handleSubmit = (e,playList) => {
        e.preventDefault();
        setSelectPlayList(playList);
  };
  const params = {
        "playlist_id": selectPlayList,
        "track_number" : track && track[0]["track_info"]["track_number"],
        "name" : track && track[0]["track_info"]["name"],
        "album" : track && track[0]["album_info"]["name"],
        "duration": track && track[0]["track_info"]["duration_ms"],
        "spotify_id": track && track[0]["track_info"]["id"],
        "artist" : track && track[0]["track_info"]["artist_name"]
    }

  console.log(params);
  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }
  return (
    data && (
      <div className="row mt-3">
        <h1>
          <img
            src={track[0]["album_info"]["image"]}
            width="200px"
            height="200px"
          ></img>
        </h1>
        <h1>
          <Link
            className="link-primary text-decoration-none"
            to={`/album-info/${track[0]["album_info"]["id"]}`}
            state={{ data: { track } }}
          >
            {track[0]["album_info"]["name"]}
          </Link>
        </h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>#</th>
              <th>Title</th>
              <th>Artist</th>
              <th>Length</th>
              <th>Select Playlist</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
              <tr>
              <td>{track[0]["track_info"]["track_number"]}</td>
              <td>{track[0]["album_info"]["name"]}</td>
              <td>
                <div>
                  {track[0]["track_info"]["artist_name"].map((artist) => (
                    <React.Fragment key={artist.id}>
                      <Link to={`/artist-info/${artist.artist_id}`}>
                        {artist.artist_name}
                      </Link>
                      <br />
                    </React.Fragment>
                  ))}
                </div>
              </td>
              <td>{track[0]["track_info"]["duration_ms"]}</td>
              <td>
              <div className="dropdown">
              <Dropdown>
                <Dropdown.Toggle variant="secondary" id="dropdownMenuButton">
                  Playlists
                </Dropdown.Toggle>
                <Dropdown.Menu>
                  {playLists &&
                    playLists?.playlists.map((playlist) => (
                      <Dropdown.Item key={playlist.id} onClick={(e) => handleSubmit(e, playlist.id)}>
                        {playlist.name}
                      </Dropdown.Item>
                    ))}
                </Dropdown.Menu>
              </Dropdown>
            </div>
            </td>
            <td><CreateSongButton data = {params} /></td>
            </tr>
          </tbody>
        </table>
      </div>
    )
  );
};
export default SongInfo;
