import { useSelector } from "react-redux";
import { useGetSpotifyArtistQuery } from "./app/apiSlice";
import DeletePlayListButton from "./playlist_functions/PlayListFunctions";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import SearchAlbums from "./SearchAlbums";

function ArtistPage() {
  const location = useLocation();
  const { data } = location.state;
  console.log("!!!!!!!!!!!!!!", data);

  const params = {
    type: "artist,album,track",
    limit: 4,
    offset: 0,
  };

  const { data: artistData, error, isLoading } = useGetSpotifyArtistQuery(data);

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }
  // return <>Loading...</>;
  return (
    <div className="row mt-3">
      <h1>{data.artist["name"]}</h1>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Albums</th>
            <th>id</th>
          </tr>
        </thead>
        <tbody>
          {data[0]?.artist_info.map((artist) => {
            return (
              <tr key={artist.id}>
                <td>{artist.name}</td>
                <td>{artist.name}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default ArtistPage;
