import React, { useState, useEffect } from "react";
import {
  useLazyGetSpotifyRecommendationsQuery,
  useGetAllGenreQuery,
} from "./app/apiSlice";
import { Dropdown } from "react-bootstrap";
import { Button } from "react-bootstrap";
import { skipToken } from "@reduxjs/toolkit/query/react";
import { Link } from "react-router-dom";

function Recommendations() {
  // const [skip, setSkip] = React.useState(true)
  const [inputValue, setInputValue] = useState("");
  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };
  const { data: genres } = useGetAllGenreQuery();
  const [selectGenre, setSelectGenre] = useState("");
  // const updateSubmitted = (e) => {
  // e.preventDefault();
  // setSubmitted(!submitted);
  // }
  const params = {
    genre: selectGenre,
    artist: inputValue,
  };
  // debounce
  const [trigger, { data }] = useLazyGetSpotifyRecommendationsQuery();
  //   const { data, isUninitialized } = useGetSpotifyRecommendationsQuery(params,
  //     {
  //     skip,
  //   }
  // );

  const handleTrigger = () => {
    trigger(params);
  };

  const handleSubmit = (e, genre) => {
    e.preventDefault();
    setSelectGenre(genre);
  };

  return (
    <div>
      <form>
        <div>
          <input
            type="text"
            id="seedArtist"
            value={inputValue}
            onChange={handleInputChange}
          />
          <Button variant="primary" onClick={handleTrigger}>
            Search
          </Button>
        </div>
      </form>
      <div className="dropdown">
        <Dropdown>
          <Dropdown.Toggle variant="secondary" id="dropdownMenuButton">
            Genre
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {genres &&
              genres[0]?.genres.map((genre) => (
                <Dropdown.Item
                  key={genre}
                  onClick={(e) => handleSubmit(e, genre)}
                >
                  {genre}
                </Dropdown.Item>
              ))}
          </Dropdown.Menu>
        </Dropdown>
      </div>

      {data && (
        <div className="row mt-3">
          <h1>Recommendation</h1>
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Image</th>
                <th>Name</th>
              </tr>
            </thead>
            <tbody>
              {data[0]?.map((track) => {
                return (
                  <tr key={track.id}>
                    <td>
                      <Link
                        className="link-primary text-decoration-none"
                        to={`/spotify/tracks/${track.id}`}
                        state={{ data: { track } }}
                      >
                        <img
                          src={track.image}
                          width="90px"
                          height="90px"
                          alt=""
                        />
                      </Link>
                    </td>
                    <td>
                      <Link
                        className="link-primary text-decoration-none"
                        to={`/spotify/tracks/${track.id}`}
                        state={{ data: { track } }}
                      >
                        {track.name}
                      </Link>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default Recommendations;
