import { useSelector } from "react-redux";
import { useGetSpotifyRandomQuery } from "./app/apiSlice";
import { useNavigate } from "react-router";
import { Button } from "react-bootstrap";
import React from "react";
import {
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBBtn,
} from "mdb-react-ui-kit";
import "./App.css";

function SpotifyRandom() {
  // const searchCriteria = useSelector((state) => state.search.value);
  const { data, error, isLoading } = useGetSpotifyRandomQuery();
  console.log({ data, error, isLoading });

  const navigate = useNavigate();

  if (isLoading) return <>Loading...</>;

  return (
    <div>
      {data["rickrolld"] ? (
        <div className="rickroll-background">
          <div
            id="rick-text"
            className="ct-animate-blink "
            height="100%"
            width="100%"
          >
            {data["rickrolld"]}
          </div>
          <div className="container-video">
            <iframe
              width="100%"
              height="300"
              scrolling="no"
              frameborder="no"
              allow="autoplay"
              src="https://w.soundcloud.com/player/?url=https%3A//api.soundcloud.com/tracks/1242868615&color=%232c2f47&auto_play=true&hide_related=false&show_comments=true&show_user=true&show_reposts=false&show_teaser=true&visual=true"
            ></iframe>
            <div style="font-size: 10px; color: #cccccc;line-break: anywhere;word-break: normal;overflow: hidden;white-space: nowrap;text-overflow: ellipsis; font-family: Interstate,Lucida Grande,Lucida Sans Unicode,Lucida Sans,Garuda,Verdana,Tahoma,sans-serif;font-weight: 100;">
              <Link
                href="https://soundcloud.com/rick-astley-official"
                title="Rick Astley"
                target="_blank"
                style="color: #cccccc; text-decoration: none;"
              >
                Rick Astley
              </Link>
              Â·
              <Link
                href="https://soundcloud.com/rick-astley-official/never-gonna-give-you-up-4"
                title="Never Gonna Give You Up"
                target="_blank"
                style="color: #cccccc; text-decoration: none;"
              >
                Never Gonna Give You Up
              </Link>
            </div>
          </div>
        </div>
      ) : (
        <MDBCol sm="4">
          <MDBCard>
            <MDBCardImage src={data.album_image} position="top" alt="..." />
            <MDBCardBody>
              <MDBCardTitle>{data.track_name}</MDBCardTitle>
              <MDBCardText>Artist: {data.artist_name}</MDBCardText>
              <MDBCardText>Album: {data.album_name}</MDBCardText>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      )}
      <Button
        onClick={() => navigate(0)}
        className="btn-primary"
        id="rick-button"
      >
        Try Again!
      </Button>
    </div>
  );
}

export default SpotifyRandom;
