import { useGetSpotifyTrackQuery } from "./app/apiSlice";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";

const SongInfo = () => {
  const location = useLocation();
  const { data } = location.state;

  let id;
  if (data.track) {
    id = data.track["id"];
  } else if (data.album) {
    id = data.album["id"];
  }

  const { data: track, isLoading } = useGetSpotifyTrackQuery(id);

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }
  console.log(data.track[0]["album_info"]["image"]);
  return (
    data && (
      <div className="row mt-3">
        <h1>
          <img
            src={track[0]["album_info"]["image"]}
            width="200px"
            height="200px"
          ></img>
        </h1>
        <h1>
          <Link
            to={`/album-info/${track[0]["album_info"]["id"]}`}
            state={{ data: { track } }}
          >
            {track[0]["album_info"]["name"]}
          </Link>
        </h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>#</th>
              <th>Title</th>
              <th>Artist</th>
              <th>Length</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{track[0]["track_info"]["track_number"]}</td>
              <td>{track[0]["track_info"]["name"]}</td>
              <td>{track[0]["track_info"]["artist_name"][0]["artist_name"]}</td>
              <td>{track[0]["track_info"]["duration_ms"]}</td>
            </tr>
          </tbody>
        </table>
      </div>
    )
  );
};
export default SongInfo;
