import { useDispatch, useSelector } from "react-redux";
import { useSearchSpotifyQuery } from "./app/apiSlice";
import { useLocation } from "react-router-dom";

function SearchTracks() {
  const sSpotify = useSelector((state) => state.search.value);

  const params = {
    search: sSpotify,
    type: "artist,album,track",
    limit: 4,
    offset: 0,
  };
  const { data, isLoading } = useSearchSpotifyQuery(params);

  const bro = data && data[0][1];
  console.log("?????????????????????????", bro);

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }

  return (
    data && (
      <div className="row mt-3">
        <h1>Related Tracks</h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Album Image</th>
              <th>Name</th>
              <th>Artist</th>
              <th>Duration</th>
            </tr>
          </thead>
          <tbody>
            {data[0][1]?.tracks_info.map((track) => {
              const artistNames = track.artist_name
                .map((artist) => artist.artists_name)
                .join(", ");
              return (
                <tr key={track.id}>
                  <td>
                    <img
                      src={track.album_image}
                      width="90px"
                      height="90px"
                    ></img>
                  </td>
                  <td>{track.name}</td>
                  <td>{artistNames}</td>
                  <td>{new Date(track.duration_ms).getMinutes()}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    )
  );
}
export default SearchTracks;
