import { useDispatch, useSelector } from "react-redux";
import { useGetSpotifyArtistQuery } from "./app/apiSlice";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";

function AlbumInfo() {
  const location = useLocation();
  const { data } = location.state;
  console.log(data);
  const { data: artist, isLoading } = useGetSpotifyArtistQuery(
    data.artist["id"]
  );
  const bro = data && data[0][1];
  console.log("?????????????????????????", bro);

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }

  return (
    data && (
      <div className="row mt-3">
        <h1>Related Tracks</h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Album Image</th>
              <th>Name</th>
              <th>Artist</th>
              <th>Duration</th>
            </tr>
          </thead>
          <tbody>
            {data[0][1]?.tracks_info.map((track) => {
              const artistNames = track.artist_name
                .map((artist) => artist.artists_name)
                .join(", ");
              return (
                <tr key={track.id}>
                  <td>
                    <img
                      src={track.album_image}
                      width="90px"
                      height="90px"
                    ></img>
                  </td>
                  <td>{track.name}</td>
                  <td>{artistNames}</td>
                  <td>{track.duration_ms}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    )
  );
}
export default AlbumInfo;
