import { useDispatch, useSelector } from "react-redux";
import { useSearchSpotifyQuery } from "./app/apiSlice";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";

function SearchAlbums() {
  const sSpotify = useSelector((state) => state.search.value);

  const params = {
    search: sSpotify,
    type: "artist,album,track",
    limit: 4,
    offset: 0,
  };
  const { data, isLoading } = useSearchSpotifyQuery(params);

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }

  return (
    data && (
      <div className="row mt-3">
        <h1>Related Albums</h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Image</th>
              <th>Name</th>
              <th>Release Date</th>
              <th>Total Tracks</th>
            </tr>
          </thead>
          <tbody>
            {data[0][2]?.albums_info.map((album) => {
              return (
                <tr key={album.id}>
                  <td>
                    <img src={album.image} width="90px" height="90px"></img>
                  </td>
                  <td>
                    <Link
                      to={`/album-info/${album.id}`}
                      state={{ data: { albums } }}
                    >
                      {album.name}
                    </Link>
                  </td>
                  <td>{album.release_date}</td>
                  <td>{album.total_tracks}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    )
  );
}
export default SearchAlbums;
