import { NavLink } from "react-router-dom";
import { useGetTokenQuery, useLogoutMutation } from "./app/apiSlice";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Search from "./Search"



function NavBar() {
  const { data: account } = useGetTokenQuery();
  const [logout, logoutResponse] = useLogoutMutation();
  const navigate = useNavigate();
  // console.log({ account });
  // console.log(logoutResponse);

  useEffect(() => {
    if (logoutResponse.data) {
      navigate(`${"HomePage"}`);
    }
  }, [logoutResponse]);

  return (
    <nav
      className="navbar navbar-expand-lg bg-body-tertiary"
      data-bs-theme="dark"
    >
      <div className="container-fluid">
        <NavLink to={"/"} className="navbar-brand">
          Stupify
        </NavLink>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <NavLink
                to={"HomePage"}
                className={"nav-link"}
                aria-current="page"
              >
                Home
              </NavLink>
            </li>
            {account && (
              <li className="nav-item">
                <NavLink to="allplaylist" className={"nav-link"}>
                  Playlists
                </NavLink>
              </li>
            )}
            {account && (
              <li className="nav-item">
                <NavLink
                to={"/spotify/allgenres"}
                className={"nav-link"}>
                  Genres
                </NavLink>
              </li>
            )}
            {!account && (
              <li className="nav-item">
                <NavLink to={"LoginPage"} className={"nav-link"}>
                  Login
                </NavLink>
              </li>
            )}
            {!account && (
              <li className="nav-item">
                <NavLink to={"SignupPage"} className={"nav-link"}>
                  Sign Up
                </NavLink>
              </li>
            )}
            {/* if you do not have account use this ^ format ln 61 - 67 */}
          </ul>
          <NavLink className="navbar-brand"></NavLink>
          <form className="d-flex" role="search">
            <input
              className="form-control me-2"
              type="search"
              placeholder="Search"
              aria-label="Search"
            />
            <button className="btn btn-secondary" type="submit">
              Search
            </button>
          </form>
          {account && (
            <button
              className="btn btn-danger"
              to="logoutPage"
              onClick={() => logout()}
            >
              Logout
            </button>
          )}
          {/* if you have an account use this ^ format {account && ...} & onclick if you need to pass in multiple things you can {() => logout()} */}
        </div>
      </div>
    </nav>
  );
}

export default NavBar;
