import { NavLink } from "react-router-dom";
import { useGetTokenQuery, useLogoutMutation } from "./app/apiSlice";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Search from "./Search";
import { Link } from "react-router-dom";

function NavBar() {
  const { data: account } = useGetTokenQuery();
  const [logout, logoutResponse] = useLogoutMutation();
  const navigate = useNavigate();
  // console.log({ account });
  // console.log(logoutResponse);

  useEffect(() => {
    if (logoutResponse.data) {
      navigate(`${"HomePage"}`);
    }
  }, [logoutResponse]);

  return (
    <nav
      className="navbar navbar-expand-lg bg-body-tertiary"
      data-bs-theme="dark"
    >
      <div className="container-fluid">
        <NavLink to={"logopage"} className="navbar-brand">
          <div
            style="position: relative; width: 100%; height: 0; padding-top: 100.0000%;
 padding-bottom: 0; box-shadow: 0 2px 8px 0 rgba(63,69,81,0.16); margin-top: 1.6em; margin-bottom: 0.9em; overflow: hidden;
 border-radius: 8px; will-change: transform;"
          >
            <iframe
              loading="lazy"
              style="position: absolute; width: 100%; height: 100%; top: 0; left: 0; border: none; padding: 0;margin: 0;"
              src="https:&#x2F;&#x2F;www.canva.com&#x2F;design&#x2F;DAFtzlmKCEg&#x2F;view?embed"
              allowfullscreen="allowfullscreen"
              allow="fullscreen"
            ></iframe>
          </div>
          <Link
            href="https:&#x2F;&#x2F;www.canva.com&#x2F;design&#x2F;DAFtzlmKCEg&#x2F;view?utm_content=DAFtzlmKCEg&amp;utm_campaign=designshare&amp;utm_medium=embeds&amp;utm_source=link"
            target="_blank"
            rel="noopener"
          >
            Stupify
          </Link>
        </NavLink>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            {/* <li className="nav-item">
              <NavLink
                to={"HomePage"}
                className={"nav-link"}
                aria-current="page"
              >
                Home
              </NavLink>
            </li> */}
            {account && (
              <li className="nav-item">
                <NavLink to={"HomePage"} className={"nav-link"}>
                  Home
                </NavLink>
              </li>
            )}

            {account && (
              <li className="nav-item">
                <NavLink to="allplaylist" className={"nav-link"}>
                  Playlists
                </NavLink>
              </li>
            )}
            {account && (
              <li className="nav-item">
                <NavLink to={"/spotify/allgenres"} className={"nav-link"}>
                  Genres
                </NavLink>
              </li>
            )}
            {account && (
              <li className="nav-item">
                <NavLink to={"/spotify/recommendations"} className={"nav-link"}>
                  Recommendations
                </NavLink>
              </li>
            )}
            {account && (
              <li className="nav-item">
                <NavLink to={"/spotify/random"} className={"nav-link"}>
                  Random
                </NavLink>
              </li>
            )}
            {!account && (
              <li className="nav-item">
                <NavLink to={"LoginPage"} className={"nav-link"}>
                  Login
                </NavLink>
              </li>
            )}
            {!account && (
              <li className="nav-item">
                <NavLink to={"SignupPage"} className={"nav-link"}>
                  Sign Up
                </NavLink>
              </li>
            )}
            {/* if you do not have account use this ^ format ln 61 - 67 */}
          </ul>

          {account && (
            <button
              className="btn btn-danger"
              to="logoutPage"
              onClick={() => logout()}
            >
              Logout
            </button>
          )}
          {/* if you have an account use this ^ format {account && ...} & onclick if you need to pass in multiple things you can {() => logout()} */}
        </div>
      </div>
    </nav>
  );
}

export default NavBar;
