import { useGetSpotifyAlbumQuery } from "./app/apiSlice";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";

const AlbumInfo = () => {
  const location = useLocation();
  const { data } = location.state;

  let id;
  if (data.track) {
    id = data.track[0]["album_info"]["id"];
  } else if (data.artist) {
    id = data.artist["id"];
  } else {
    id = data.album["id"];
  }

  const { data: albums, isLoading } = useGetSpotifyAlbumQuery(id);

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }

  return (
    data && (
      <div className="row mt-3">
        <h1>
          <img
            src={albums[0]["album_info"]["image"]}
            width="200px"
            height="200px"
          ></img>
        </h1>
        <h1>{albums[0]["album_info"]["name"]}</h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>#</th>
              <th>Name</th>
              <th>Length</th>
            </tr>
          </thead>
          <tbody>
            {albums[0]?.tracks.map((track) => {
              return (
                <tr key={track.id}>
                  <td>{track.track_number}</td>
                  <td>
                    <Link
                      to={`/track-info/${track.id}`}
                      state={{ data: { track } }}
                    >
                      {track.name}
                    </Link>
                  </td>
                  <td>
                    {Math.floor(track.duration_ms / 60000)}:
                    {((track.duration_ms / 1000) % 60).toFixed()}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    )
  );
};
export default AlbumInfo;
