import { useGetAllSongsInPlaylistQuery } from "./app/apiSlice";
import DeleteSongButton from "./playlist_functions/DeleteSong.js";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";

function SpecificPlayList() {
  const location = useLocation();
  const { data } = location.state;

  const {
    data: playlistData,
    error,
    isLoading,
  } = useGetAllSongsInPlaylistQuery(data.playlist["id"]);

  console.log("??????????????????????", playlistData && playlistData);

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }
  // return <>Loading...</>;

  return (
    <div className="row mt-3">
      <h1>{data.playlist["name"]}</h1>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>#</th>
            <th>Title</th>
            <th>Artist</th>
            <th>Album</th>
            <th>Duration</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {playlistData?.songs.map((song, index) => {
            const artistLinks = song.artist.map((artist) => (
              <Link key={artist.id} to={`/artist-info/${artist.id}`}>
                {artist.artist_name}
                <br />
              </Link>
            ));
            return (
              <tr key={song.id}>
                <td>{index + 1}</td>
                <td>{song.name}</td>
                <td>{artistLinks}</td>
                <td>{song.album}</td>
                <td>
                  {Math.floor(song.duration_ms / 60000)}:
                  {((song.duration_ms / 1000) % 60).toFixed().padStart(2, "0")}
                </td>
                <td>
                  <DeleteSongButton sId={song.id} pId={data.playlist["id"]} />
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
export default SpecificPlayList;
