import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import {
  useLazyGetSpotifyRecommendationsQuery,
  useGetAllGenreQuery,
} from "./app/apiSlice";
import { Dropdown } from "react-bootstrap";
import { Button } from "react-bootstrap";
import { skipToken } from "@reduxjs/toolkit/query/react";
import { Link } from "react-router-dom";

function Recommendations() {
  // const [skip, setSkip] = React.useState(true)
  const [inputValue, setInputValue] = useState("");
  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };
  const { data: genres } = useGetAllGenreQuery();
  const [selectGenre, setSelectGenre] = useState("");
  // const updateSubmitted = (e) => {
  // e.preventDefault();
  // setSubmitted(!submitted);
  // }
  const params = {
    genre: selectGenre,
    artist: inputValue,
  };
  // debounce
  const [trigger, { data }] = useLazyGetSpotifyRecommendationsQuery();
  //   const { data, isUninitialized } = useGetSpotifyRecommendationsQuery(params,
  //     {
  //     skip,
  //   }
  // );
  console.log("???????????????????????????", data);

  const handleTrigger = () => {
    trigger(params);
  };

  const handleSubmit = (e, genre) => {
    e.preventDefault();
    setSelectGenre(genre);
  };

  return (
    <div className="card text-end">
      <div className="card-body">
        <form className="row align-items-center">
          <div className="col-md-8 mb-3">
            <input
              className="form-control form-control-lg form-control"
              type="text"
              id="seedArtist"
              placeholder="Enter genre & select from dropdown"
              value={inputValue}
              onChange={handleInputChange}
            />
          </div>
          <div className="col-md-2 mb-3">
            <Button variant="primary" onClick={handleTrigger}>
              Search
            </Button>
          </div>
          <div className="col-md-1 mb-3">
            <Dropdown>
              <Dropdown.Toggle variant="secondary" id="dropdownMenuButton">
                Genre
              </Dropdown.Toggle>
              <Dropdown.Menu>
                {genres &&
                  genres[0]?.genres.map((genre) => (
                    <Dropdown.Item
                      key={genre}
                      onClick={(e) => handleSubmit(e, genre)}
                    >
                      {genre}
                    </Dropdown.Item>
                  ))}
              </Dropdown.Menu>
            </Dropdown>
          </div>
        </form>

        {data && (
          <div className="text-start">
            <h1>Recommendations:</h1>
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>Image</th>
                  <th>Name</th>
                </tr>
              </thead>
              <tbody>
                {data[0]?.map((track) => {
                  return (
                    <tr key={track.id}>
                      <td>
                        <Link
                          className="link-primary text-decoration-none"
                          to={`/track-info/${track.id}`}
                          state={{ data: { track } }}
                        >
                          <img
                            src={track.image}
                            width="90px"
                            height="90px"
                            alt=""
                          />
                        </Link>
                      </td>
                      <td>
                        <Link
                          className="link-primary text-decoration-none"
                          to={`/track-info/${track.id}`}
                          state={{ data: { track } }}
                        >
                          {track.name}
                        </Link>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default Recommendations;
