import { useDispatch, useSelector } from "react-redux";
import { useGetSpotifyArtistQuery } from "./app/apiSlice";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import AlbumInfo from "./AlbumInfo";

function ArtistPage() {
  const location = useLocation();
  const { data } = location.state;
  console.log(data);
  const { data: artist, isLoading } = useGetSpotifyArtistQuery(
    data.artist["id"]
  );
  //   console.log(artist);
  //   console.log(data && data[0][2]["albums_info"]);
  //   const bro = data && data[0][1]["tracks_info"];
  //   console.log("?????????????????????????", bro);

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }

  return (
    data && (
      <div className="row mt-3">
        <h1>
          <img
            src={artist[0]["artist_info"][0]["image"]}
            width="200px"
            height="200px"
          ></img>
        </h1>
        <h1>{artist[0]["artist_info"][0]["name"]}</h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Albums</th>
              <th>Songs</th>
              <th>Title</th>
              <th>Total Tracks</th>
              <th>Release date</th>
            </tr>
          </thead>
          <tbody>
            {artist[0]?.albums_info.map((albums) => {
              console.log(albums.id);
              return (
                <tr key={albums.id}>
                  <td>
                    <img src={albums.image} width="90px" height="90px"></img>
                  </td>
                  <td>
                    {" "}
                    <div class="dropdown">
                      <button
                        class="btn btn-secondary dropdown-toggle"
                        type="button"
                        data-bs-toggle="dropdown"
                        aria-expanded="false"
                      >
                        Dropdown button
                      </button>
                      <ul class="dropdown-menu">
                        <li>
                          <a class="dropdown-item" href="#">
                            Action
                          </a>
                        </li>
                        <li>
                          <a class="dropdown-item" href="#">
                            Another action
                          </a>
                        </li>
                        <li>
                          <a class="dropdown-item" href="#">
                            Something else here
                          </a>
                        </li>
                      </ul>
                    </div>{" "}
                  </td>
                  <td>{albums.name}</td>
                  {/* <td>
                    <Link
                      to={`/album-info/${albums.id}`}
                      state={{ data: { albums } }}
                    >
                      {albums.name}
                    </Link>
                  </td> */}
                  <td>{albums.total_tracks}</td>
                  <td>{albums.release_date}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    )
  );
}
export default ArtistPage;
