import { useGetSpotifyArtistQuery } from "./app/apiSlice";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";

function ArtistPage() {
  const location = useLocation();
  const { data } = location.state;

  const { data: artist, isLoading } = useGetSpotifyArtistQuery(
    data.artist["id"]
  );

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }

  return (
    data && (
      <div className="row mt-3">
        <h1>
          <img
            src={artist[0]["artist_info"][0]["image"]}
            width="200px"
            height="200px"
          ></img>
        </h1>
        <h1>{artist[0]["artist_info"][0]["name"]}</h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Albums</th>
              <th>Title</th>
              <th>Total Tracks</th>
              <th>Release date</th>
            </tr>
          </thead>
          <tbody>
            {artist[0]?.albums_info.map((album) => {
              return (
                <tr key={album.id}>
                  <td>
                    <Link
                      to={`/album-info/${album.id}`}
                      state={{ data: { album } }}
                    >
                      <img src={album.image} width="90px" height="90px"></img>
                    </Link>
                    {/* <img src={album.image} width="90px" height="90px"></img> */}
                  </td>
                  <td>
                    <Link
                      className="link-dark"
                      to={`/album-info/${album.id}`}
                      state={{ data: { album } }}
                    >
                      {album.name}
                    </Link>
                  </td>
                  <td>{album.total_tracks}</td>
                  <td>{album.release_date}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    )
  );
}
export default ArtistPage;
