import { useDispatch, useSelector } from "react-redux";
import { useSearchSpotifyQuery } from "./app/apiSlice";
import { useLocation } from 'react-router-dom';


function SearchPage() {

    const sSpotify = useSelector((state) => state.search.value);

    const params = {
            "search": sSpotify,
            "type": "artist,album,track",
            "limit": 4,
            "offset": 0,
        }
    const {data, isLoading} = useSearchSpotifyQuery(params);


  if (isLoading) {return (
            <progress className="progress is-primary" max="100"></progress>
        );}

  return (
    (data && <div className="row mt-3">
      <h1>Related Artists</h1>
            <table className="table table-striped">
        <thead>
          <tr>
            <th>Image</th>
            <th>Name</th>
          </tr>
        </thead>
        <tbody>
          {data[0][0]?.artists_info.map(artist => {
            return (
                <tr key={artist.id}>
                <td><img src={artist.artist_image} width={150} height={150} ></img></td>
                <td>{artist.name}</td>
              </tr>);
          })}
        </tbody>
      </table>
    </div>

  )
  );
}
export default SearchPage;
