import { useSelector } from "react-redux";
import { useSearchSpotifyGenreQuery, useGetAllPlaylistQuery, useGetAllSongsInPlaylistQuery, useDeletePlaylistMutation } from "./app/apiSlice";
import { useLocation } from 'react-router-dom';


function GenreArtists() {
    const location = useLocation();
    const { data } = location.state
    console.log(data.genre)

    const params = {
    search: data.genre,
    type: "artist,album,track",
    limit: 10,
    };

    const { data: genreArtistsData, error, isLoading } = useSearchSpotifyGenreQuery(params);

    console.log(genreArtistsData)



  if (isLoading) {return (
            <progress className="progress is-primary" max="100"></progress>
        );}

  return (
    <div className="row mt-3">
      <h1>Best {data.genre} Artists</h1>
            <table className="table table-striped">
        <thead>
          <tr>
            <th>Image</th>
            <th>Name</th>
          </tr>
        </thead>
        <tbody>
          {genreArtistsData[0]?.map(artist => {
            return (
                <tr key={artist.id}>
                <td><img src={artist.artist_image} width="90px" height="90px"></img></td>
                <td>{artist.name}</td>
              </tr>);
          })}
        </tbody>
      </table>
    </div>
  );
}
export default GenreArtists;
