import React, { useState, useEffect } from "react";
import {
  useLazyGetSpotifyRecommendationsQuery,
  useGetAllGenreQuery,
} from "./app/apiSlice";
import { Dropdown } from "react-bootstrap";
import { Button } from "react-bootstrap";
import { skipToken } from "@reduxjs/toolkit/query/react";
import { Link } from "react-router-dom";

function Recommendations() {
  const [inputValue, setInputValue] = useState("");
  const [selectedGenre, setSelectedGenre] = useState("");
  const [genre, setGenres] = useState([]);

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);
  };

  const handleGenreSelect = (genre) => {
    setSelectedGenre(genre);
  };

  const { data: genres } = useGetAllGenreQuery();
  const [selectGenre, setSelectGenre] = useState("");

  const params = {
    genre: selectGenre,
    artist: inputValue,
  };
  const [trigger, { data }] = useLazyGetSpotifyRecommendationsQuery();

  const handleTrigger = () => {
    trigger(params);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("InputValue:", inputValue);
    console.log("Selected Genre:", selectedGenre);
  };

  return (
    <div className="card text-end">
      <div className="card-body">
        <form className="row align-items-center">
          <div className="col-md-8 mb-3">
            <input
              className="form-control form-control-lg form-control"
              type="text"
              id="seedArtist"
              placeholder="Enter genre & select from dropdown"
              value={inputValue}
              onChange={handleInputChange}
            />
          </div>

          <div className="col-md-1 mb-3">
            <Dropdown>
              <Dropdown.Toggle
                variant="secondary btn-lg"
                id="dropdownMenuButton"
              >
                Genre
              </Dropdown.Toggle>
              <Dropdown.Menu>
                {genres &&
                  genres[0]?.genres.map((genre) => (
                    <Dropdown.Item
                      key={genre}
                      onClick={(e) => handleGenreSelect(genre)}
                    >
                      {genre}
                    </Dropdown.Item>
                  ))}
              </Dropdown.Menu>
            </Dropdown>
          </div>
          <div className="col-md-2 mb-3">
            <Button variant="success btn-lg" onClick={handleTrigger}>
              Search
            </Button>
          </div>
        </form>
        <div></div>

        {data && (
          <div className="text-start">
            <h1>
              you chose: {inputValue} & {selectedGenre}
            </h1>
            <h2> recommendations</h2>
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>Image</th>
                  <th>Name</th>
                </tr>
              </thead>
              <tbody>
                {data[0]?.map((track) => {
                  return (
                    <tr key={track.id}>
                      <td>
                        <Link
                          className="link-primary text-decoration-none"
                          to={`/track-info/${track.id}`}
                          state={{ data: { track } }}
                        >
                          <img
                            src={track.image}
                            width="90px"
                            height="90px"
                            alt=""
                          />
                        </Link>
                      </td>
                      <td>
                        <Link
                          className="link-primary text-decoration-none"
                          to={`/track-info/${track.id}`}
                          state={{ data: { track } }}
                        >
                          {track.name}
                        </Link>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default Recommendations;
