import { useSelector } from "react-redux";
import { useGetSpotifyArtistQuery } from "./app/apiSlice";
import DeletePlayListButton from "./playlist_functions/PlayListFunctions";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import AboutArtist from "./AboutArtist";

function ArtistPage() {
  const location = useLocation();
  const { data } = location.state;
  const sSpotify = useSelector((state) => state.search.value);
  console.log("!!!!!!!!!!!!!!", data);

  const params = {
    search: sSpotify,
    type: "artist,album,track",
    limit: 5,
    offset: 0,
  };

  const {
    //   data: artistData,
    error,
    isLoading,
  } = useGetSpotifyArtistQuery(params);
  console.log(artistData);

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }
  // return <>Loading...</>;
  return (
    data && (
      <div className="row mt-3">
        <h1>{data.artist["name"]}</h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Albums</th>
              <th>id</th>
            </tr>
          </thead>
          <tbody>
            {data[0]?.artists.map((artist) => {
              return (
                <tr key={artist.id}>
                  <td>{artist.name}</td>
                  <td>
                    <AboutArtist />
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    )
  );
}

export default ArtistPage;
