import { useSelector } from "react-redux";
import { useGetSpotifyRandomQuery } from "./app/apiSlice";
import { useNavigate } from "react-router";
import { Button } from "react-bootstrap";
import React from "react";
import {
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBBtn,
} from "mdb-react-ui-kit";
import "./App.css";
import { Link } from "react-router-dom";

function SpotifyRandom() {
  // const searchCriteria = useSelector((state) => state.search.value);
  const { data, error, isLoading } = useGetSpotifyRandomQuery();
  console.log({ data, error, isLoading });

  const navigate = useNavigate();

  if (isLoading) return <>Loading...</>;

  return (
    <div>
      {data["rickrolld"] ? (
        <div className="rickroll-background">
          <div
            id="rick-text"
            className="ct-animate-blink "
            height="100%"
            width="100%"
          >
            {data["rickrolld"]}
          </div>
          <div className="center-container">
      <Button
        onClick={() => navigate(0)}
        className="btn-primary"

      >
        Try Again!
      </Button>
    </div>
        </div>
      ) : (
        <div className="center-container">
        <MDBCol sm="4">
          <MDBCard>
            <MDBCardImage src={data.album_image} position="top" alt="..." />
            <MDBCardBody>
              <MDBCardTitle>
                <Link className="link-primary text-decoration-none" to={`/spotify/tracks/${data.track_id}`}>
                {data.track_name}
              </Link></MDBCardTitle>
              <MDBCardText>Artist: <Link className="link-primary text-decoration-none" to={`/spotify/artists/${data.artist_id}`}>
                {data.artist_name}
              </Link></MDBCardText>
              <MDBCardText>Album: <Link className="link-primary text-decoration-none" to={`/spotify/albums/${data.album_id}`}>
                {data.album_name}
              </Link>
              </MDBCardText>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
        <div>
        <br>
        </br>
          <Button
        onClick={() => navigate(0)}
        className="btn-primary"
        // id="rick-button"
      >
        Try Again!
      </Button>
        </div>
        </div>

      )}
      {/* <div className="center-container">
      <Button
        onClick={() => navigate(0)}
        className="btn-primary"
        id="rick-button"
      >
        Try Again!
      </Button>
    </div> */}
    </div>
  );
}

export default SpotifyRandom;
