import { useSelector } from "react-redux";
import { useGetSpotifyRandomQuery } from "./app/apiSlice";
import { useNavigate } from 'react-router'
import { Button } from 'react-bootstrap';
import React from 'react';
import {
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBBtn
} from 'mdb-react-ui-kit';
import "./App.css";


function SpotifyRandom() {
  // const searchCriteria = useSelector((state) => state.search.value);
  const { data, error, isLoading } = useGetSpotifyRandomQuery();
  console.log({ data, error, isLoading });

  const navigate = useNavigate();


  if (isLoading) return <>Loading...</>;


  return (
    <div>
      {data["rickrolld"] ? (
      <div className="rickroll-background">
      <body className="ct-animate-blink " height="100%" width="100%" >{data["rickrolld"]} </body>
      </div>) :
      <MDBCol sm='4'><MDBCard>
      <MDBCardImage src={data.album_image} position='top' alt='...' />
      <MDBCardBody>
        <MDBCardTitle>{data.track_name}</MDBCardTitle>
        <MDBCardText>
          Artist: {data.artist_name}
        </MDBCardText>
        <MDBCardText>
          Album: {data.album_name}
        </MDBCardText>
      </MDBCardBody>
    </MDBCard>
    </MDBCol>}

      <Button  onClick={() => navigate(0) }className="btn-primary">
      Try Again!
      </Button>


      </div>
  );
}



export default SpotifyRandom;
