import { useGetSpotifyAlbumQuery } from "./app/apiSlice";
import { useLocation } from "react-router-dom";

const AlbumInfo = () => {
  const location = useLocation();
  const { data } = location.state;

  const { data: album, isLoading } = useGetSpotifyAlbumQuery(data.albums["id"]);
  console.log(album[0]["album_info"]["name"]);
  // album[0]["tracks"];
  //   console.log(data && data[0][2]["albums_info"]);
  //   const bro = data && data[0][1]["tracks_info"];
  //   console.log("?????????????????????????", bro);

  if (isLoading) {
    return <progress className="progress is-primary" max="100"></progress>;
  }

  return (
    data && (
      <div className="row mt-3">
        <h1>
          <img
            src={album[0]["album_info"]["image"]}
            width="200px"
            height="200px"
          ></img>
        </h1>
        <h1>{album[0]["album_info"]["name"]}</h1>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Albums</th>
              {/* <th>Songs</th> */}
              <th>Title</th>
              <th>Total Tracks</th>
              <th>Release date</th>
            </tr>
          </thead>
          <tbody>
            {album[0]?.album_info.map((albums) => {
              console.log(albums.id);
              return (
                <tr key={albums.id}>
                  <td>
                    <img src={albums.image} width="90px" height="90px"></img>
                  </td>
                  <td>
                    <Link
                      to={`/album-info/${albums.id}`}
                      state={{ data: { albums } }}
                    >
                      {albums.name}
                    </Link>
                  </td>
                  <td>{albums.total_tracks}</td>
                  <td>{albums.release_date}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    )
  );
};
export default AlbumInfo;
